import styled from 'styled-components'

const Card = styled.div`
    background-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.light};
`

export default function StepCard () {
    return (
        <Card>
            <h3>1. Título</h3>
            <p>Descrição.</p>
        </Card>
    )
}
